INSERT INTO `wp_posts` VALUES ('1492', '1', '2014-03-07 13:46:20', '2014-03-07 09:46:20', 'Клиент: Туристическая компания \"Меланж\"\r\n\r\nРабота над фирменным стилем новой турфирмы началась с нуля и включала нейминг, создание логотипа компании, элементов корпоративной идентификации, разработку рекламных сообщений, а также дизайн рекламных носителей.\r\nСтиль \"Меланж\" — яркий, живой, открытый, с долей юмора, рассчитан на широкую целевую аудиторию компании.\r\nРабота вошла в шорт-лист международного конкурса Identity: Best Of The Best 2008 в номинации \"Фирменный стиль для ретейла и сферы услуг\".', 'Фирменный стиль компании \"Меланж\"', '', 'publish', 'open', 'open', '', 'firmenny-j-stil-kompanii-melanzh', '', '', '2014-03-07 13:46:20', '2014-03-07 09:46:20', '', '0', 'http://dab.by/?post_type=portfolio&#038;p=1492', '0', 'portfolio', '', '0');
